gci -exclude 'Button.js','config.js','_template.js','addons.js' | % { $disp = ($_.Name).replace('.js',''); $varDecl = "const displayName = { displayName: '$disp' }`n`nstoriesOf(";  $content = get-content $_; $temp = $content.replace('storiesOf(', $varDecl).replace('))','), displayName)').replace('add(','addWithJSX('); out-file -FilePath ($_.Name) -InputObject $temp -Encoding UTF8 }
